import React, {useState,useEffect} from "react";

function Todo({ selectedProfile }) { 
  const [todos,setTodos] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const todosPerPage = 10;

  useEffect(()=>{
    const fetch = async ()=>{
      const res = await fetch('https://dummyjson.com/todos');
      const data = await res.json();
      setTodos(data.todos);
    };
    fetch();   
  }, []);

  const totalPages = Math.ceil(todos.length / todosPerPage);

  const handlePageChange = (page) =>{
    setCurrentPage(page);
  }

  const displayedTodos = todos.slice((currentPage -1) * todosPerPage, currentPage * todosPerPage);

  return (
    <div>
      <h1>Todo list (Selected Profile: {selectedProfile})</h1>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Task</th>
            <th>Complited</th>
          </tr>
        </thead>
        <tbody>
          {displayedTodos.map(todo =>(
            <tr key={todo.id}>
              <td>{todo.id}</td>
              <td>{todo.todo}</td>
              <td>{todo.completed ? "выполнено":"не выполнено"}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <div>
        {[...Array(totalPages)].map((_,index)=>{
          <button key={index+1} onClick={()=> handlePageChange(index+1)}>{index+1}</button>
        })}
      </div>
    </div>
  );
}

export default Todo;
