import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import Todo from "./Todos";      // Убедитесь, что этот путь правильный
import Profiles from "./Profile"; // Убедитесь, что этот путь правильный

function App() {
  const [selectedProfile, setSelectedProfile] = useState(() => {
    return localStorage.getItem("selectedProfile") || "";
  });

  const handleProfileChange = (profile) => {
    setSelectedProfile(profile);
    localStorage.setItem("selectedProfile", profile); // Сохраняем выбранный профиль
  };

  return (
    <Router>
      <nav>
        <Link to="/">Todo</Link>
        <Link to="/profiles">Profiles</Link>
      </nav>
      <Routes>
        <Route path="/" element={<Todo selectedProfile={selectedProfile} />} />
        <Route path="/profiles" element={<Profiles onProfileChange={handleProfileChange} />} />
      </Routes>
    </Router>
  );
}

export default App;
