import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import Todo from "./Todo"; // Убедитесь, что путь правильный
import Profiles from "./Profiles"; // Убедитесь, что путь правильный

function App() {
  const [selectedProfile, setSelectedProfile] = useState(() => {
    return localStorage.getItem("selectedProfile") || "";
  });
  const [todos, setTodos] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [profiles, setProfiles] = useState([]); // Состояние для профилей

  const handleProfileChange = (profile) => {
    setSelectedProfile(profile);
    localStorage.setItem("selectedProfile", profile); // Сохраняем выбранный профиль
  };

  useEffect(() => {
    const fetchTodosAndProfiles = async () => {
      try {
        // Получаем задачи
        const todosResponse = await fetch('https://dummyjson.com/todos');
        const todosData = await todosResponse.json();
        setTodos(todosData.todos); // Устанавливаем задачи

        // Получаем профили (пользователей) из задач
        const uniqueUserIds = [...new Set(todosData.todos.map(todo => todo.userId))];
        setProfiles(uniqueUserIds.map(id => ({ id, userId: id }))); // Создаем массив профилей
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchTodosAndProfiles(); // Запрашиваем задачи и профили
  }, []);

  useEffect(() => {
    const fetchTodosForSelectedProfile = () => {
      if (selectedProfile) {
        console.log("Fetching todos for profile:", selectedProfile); // Отладочный лог
        const filteredTodos = todos.filter(todo => todo.userId === selectedProfile); // Фильтруем задачи по userId
        setTodos(filteredTodos); // Устанавливаем отфильтрованные задачи
        setCurrentPage(1); // Сбрасываем страницу при смене профиля
      } else {
        setTodos([]); // Если профиль не выбран, очищаем задачи
      }
    };

    fetchTodosForSelectedProfile(); // Запрашиваем задачи для выбранного профиля
  }, [selectedProfile, todos]);

  return (
    <Router>
      <nav>
        <Link to="/">Todo</Link>
        <Link to="/profiles">Profiles</Link>
      </nav>
      <Routes>
        <Route path="/" element={<Todo todos={todos} currentPage={currentPage} setCurrentPage={setCurrentPage} />} />
        <Route path="/profiles" element={<Profiles profiles={profiles} onProfileChange={handleProfileChange} />} />
      </Routes>
    </Router>
  );
}

export default App;
