import { useState, useEffect } from "react";
import React from "react";
import Forms from "./Forms";
import { set } from "mongoose";

function Profiles({onProfileChange}) {

  const [profiles, setProfiles] = useState(()=>{
    const savedProfiles = localStorage.getItem("profiles");
    return savedProfiles? JSON.parse(savedProfiles):[];
  })

  const addProfile = (newProfile) =>{
    const updatedProfiles = [...profiles,newProfile];
    setProfiles(updatedProfiles);
    localStorage.getItem("profiles", JSON.stringify(updatedProfiles));
  }

  const handleProfileClick = (profile) =>{
    onProfileChange(profile);
  }

  return (
    <div>
      <h1>Select a ptofile</h1>
      <ul>
      {profiles.map((profile,index) => {
        <li onClick={() =>handleProfileClick(profile)} key={index}>{profile}</li> 
      })}
      </ul>
      <Forms onAddProfile = {addProfile}/>
    </div>
  );
}

export default Profiles;
