import { useState } from "react";
import React from "react";

function Forms({users, setSelectedUserId}) {

const [user, setUser] = useState("");

function handleSelectUser(evt){
  setUser(evt.target.value);
  const selectedUser = users.find(u=>u.userId = evt.target.value);
  if (selectedUser){
    setSelectedUserId(selectedUser.userId)
  }
}

  return (
    <div className="forms">
      <h1>Enter a userId</h1>
      <input onChange={handleSelectUser} value={user}/>
    </div>
  );
}

export default Forms;
