import React from "react";

function Todo({ todos, selectedProfile, currentPage, setCurrentPage }) {
  // Фильтруем задачи по выбранному профилю
  const filteredTodos = selectedProfile
    ? todos.filter(todo => todo.userId === selectedProfile)
    : todos;

  // Для пагинации можно использовать
  const itemsPerPage = 5; // Количество задач на странице
  const totalPages = Math.ceil(filteredTodos.length / itemsPerPage);

  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  const handlePrevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  // Отображаем только задачи на текущей странице
  const displayedTodos = filteredTodos.slice((currentPage - 1) * itemsPerPage, currentPage * itemsPerPage);

  return (
    <div>
      <h1>Todo List</h1>
      <ul>
        {displayedTodos.map(todo => (
          <li key={todo.id}>{todo.todo}</li>
        ))}
      </ul>
      <div>
        <button onClick={handlePrevPage} disabled={currentPage === 1}>Previous</button>
        <span> Page {currentPage} of {totalPages} </span>
        <button onClick={handleNextPage} disabled={currentPage === totalPages}>Next</button>
      </div>
    </div>
  );
}

export default Todo;
